local a={}function a.rgb(b)return{1/255*tonumber(string.sub(b,1,2),16),1/255*tonumber(string.sub(b,3,4),16),1/255*tonumber(string.sub(b,5,6),16)}end;function a.rgba(b)return{1/255*tonumber(string.sub(b,1,2),16),1/255*tonumber(string.sub(b,3,4),16),1/255*tonumber(string.sub(b,5,6),16),1/255*tonumber(string.sub(b,7,8),16)}end;function a.rgbo(b,c)return{1/255*tonumber(string.sub(b,1,2),16),1/255*tonumber(string.sub(b,3,4),16),1/255*tonumber(string.sub(b,5,6),16),c}end;return a